  /**
   * crush_choose_firstn - choose numrep distinct items of given type
   * @map: the crush_map
   * @bucket: the bucket we are choose an item from
   * @x: crush input value
   * @numrep: the number of items to choose
   * @type: the type of item to choose
   * @out: pointer to output vector
   * @outpos: our position in that vector
   * @out_size: size of the out vector
   * @tries: number of attempts to make
   * @recurse_tries: number of attempts to have recursive chooseleaf make
   * @local_retries: localized retries    
   * @local_fallback_retries: localized fallback retries    
   * @recurse_to_leaf: true if we want one device under each item of given type (chooseleaf instead of choose)
   * @stable: stable mode starts rep=0 in the recursive call for all replicas
   * @vary_r: pass r to recursive calls
   * @out2: second output vector for leaf items (if @recurse_to_leaf)
   * @parent_r: r value passed from the parent
   */
  static int crush_choose_firstn(const struct crush_map *map,
                     struct crush_work *work,
                     const struct crush_bucket *bucket,
                     const __u32 *weight, int weight_max,
                     int x, int numrep, int type,
                     int *out, int outpos,
                     int out_size,
                     unsigned int tries,
                     unsigned int recurse_tries,
                     unsigned int local_retries,
                     unsigned int local_fallback_retries,
                     int recurse_to_leaf,
                     unsigned int vary_r,
                     unsigned int stable,
                     int *out2,
                     int parent_r,
                                 const struct crush_choose_arg *choose_args)
  {
      int rep;    // 计数器，用来记录已经选择的数量
      unsigned int ftotal, flocal;
      int retry_descent, retry_bucket, skip_rep;
      const struct crush_bucket *in = bucket;
      int r;
      int i;
      int item = 0;
      int itemtype;
      int collide, reject;
      int count = out_size;

      dprintk("CHOOSE%s bucket %d x %d outpos %d numrep %d tries %d \
  recurse_tries %d local_retries %d local_fallback_retries %d \
  parent_r %d stable %d\n",
          recurse_to_leaf ? "_LEAF" : "",
          bucket->id, x, outpos, numrep,
          tries, recurse_tries, local_retries, local_fallback_retries,
          parent_r, stable);

      for (rep = stable ? 0 : outpos; rep  0 ; rep++) {
          /* keep trying until we get a non-out, non-colliding item */
          ftotal = 0;                    // fail total 失败的总次数
          skip_rep = 0;                // 是否跳过这一次选择
          do {
